newsTitles=["Verstappen beats Leclerc by just 0.5s in epic Saudi Arabian Grand Prix","Sainz encouraged by 'important' Saudi Arabian podium despite 'unlucky' start","Norris loses P6 by 0.1s at Jeddah, but praises improved showing from McLaren","Perez says Safety Car 'ruined' his chance of victory from pole position at Jeddah"],newsTag=["Report","News","News","News"],newsTime=["A day ago","8 hours ago","2 hours ago","3 hours ago"];const body=document.querySelector("body"),news=document.querySelector(".news"),title=document.querySelector(".title h1"),tag=document.querySelector(".tag p");function createNews(){for(let e=0;e<=3;e++){const t=document.createElement("ARTICLE");t.innerHTML=`\n      <div class="row-1">\n        <p>${newsTag[e]}</p>\n        <p>${newsTime[e]}</p>\n      </div>\n      <h2 class="title">${newsTitles[e]}</h2>\n    `,t.classList.add("new"),t.onclick=function(){cleanActive(),t.classList.add("active"),body.removeAttribute("class"),body.classList.add("bg-"+e),title.innerHTML=newsTitles[e],tag.innerHTML=newsTag[e]},news.appendChild(t)}}function cleanActive(){document.querySelectorAll(".new").forEach(e=>e.classList.remove("active"))}document.addEventListener("DOMContentLoaded",(function(){createNews(),document.querySelector(".new").classList.add("active"),body.classList.add("bg-0")}));
