newsTitles=["Dominant Leclerc beats Perez to Australian GP win as Verstappen retires","Bottas commends Alfa Romeo performance after P8 finish in Melbourne","Albon says P10 finish was 'unimaginable' before pulling off inspired strategy in Australia","Alonso 'speechless' after weekend unravels to leave him last in Melbourne"],newsTag=["Report","News","News","News"],newsTime=["10 April 2022","11 April 2022","11 April 2022","10 April 2022"];const body=document.querySelector("body"),news=document.querySelector(".news"),title=document.querySelector(".title h1"),tag=document.querySelector(".tag p");function createNews(){for(let e=0;e<=3;e++){const n=document.createElement("ARTICLE");n.innerHTML=`\n      <div class="row-1">\n        <p>${newsTag[e]}</p>\n        <p>${newsTime[e]}</p>\n      </div>\n      <h2 class="title">${newsTitles[e]}</h2>\n    `,n.classList.add("new"),n.onclick=function(){cleanActive(),n.classList.add("active"),body.removeAttribute("class"),body.classList.add("bg-"+e),title.innerHTML=newsTitles[e],tag.innerHTML=newsTag[e]},news.appendChild(n)}}function cleanActive(){document.querySelectorAll(".new").forEach(e=>e.classList.remove("active"))}document.addEventListener("DOMContentLoaded",(function(){createNews(),document.querySelector(".new").classList.add("active"),body.classList.add("bg-0")}));
